import React, { useEffect } from 'react';
import io from 'socket.io-client';

const App = () => {
  const socket = io('http://localhost:3001'); // Conectar con el servidor de Node.js

  useEffect(() => {
    // Manejar datos recibidos del servidor
    socket.on('dataFromServer', (data) => {
      console.log(`Datos del servidor: ${data}`);
      // Actualizar la interfaz de usuario con los datos recibidos
    });
  }, [socket]);

  const sendDataToServer = () => {
    const data = 'Datos desde el cliente web';
    socket.emit('dataFromClient', data); // Enviar datos al servidor de Node.js
  };

  return (
    <div>
      <button onClick={sendDataToServer}>Enviar datos al servidor</button>
    </div>
  );
};

export default App;
